			, CL_TIME = #{classTime}
			, CL_LEVEL= #{classLvl}
			, CL_RUNTIME = #{clRuntime}
			, CL_THUMBNAIL = #{clThumbnail}
			, TR_PHOTO = #{trPhoto}
			, CLASS_PHOTO = #{clPhoto}
	 WHERE
	 		CLASS_NO = #{classNo}

mapper.insert:
INSERT INTO REPORT ( REPORT_NO , REPORT_TYPE , REPORT_REASON , ISWARNED , ISCHECKED , REPORTER_ID , ITEM_CODE
) VALUES ( 'CLRP' || CL_REPORT_SEQ.NEXTVAL , #{rpReason} , #{rpDeets} , DEFAULT , DEFAULT , #{reporter} , #{classNo}
)

mapper.select:

     SELECT
     		  CLASS_NO
			, CL_NAME
			, CL_DES
			, TR_NAME
			, TR_INTRO
			, CL_CURI
			, CL_LOCA
			, CL_SHORTDES
	  FROM  COFFEECLASS

			   CL_NAME LIKE #{sv}
			OR CL_DES LIKE #{sv}
			OR TR_NAME LIKE #{sv}
			OR TR_INTRO LIKE #{sv}
			OR CL_CURI LIKE #{sv}
			OR CL_LOCA LIKE #{sv}
			OR CL_SHORTDES LIKE #{sv}					

  ORDER BY CLASS_NO

