mapper.insert:

   INSERT
   	 INTO	COFFEECLASS
   	 (
			  CLASS_NO
			, C_CODE
			, CL_NAME
			, CL_DES
			, TR_NAME
			, TR_INTRO
			, CL_CURI
			, CL_MAX
			, CL_DATE
			, CL_LOCA
			, CL_PRICE
			, BLIND
			, CL_STATUS
			, BUS_NO
			, CL_SHORTDES
			, CL_TIME
			, CL_TIME2
			, CL_TIME3
			, CL_LEVEL
			, CL_RUNTIME
			, CL_THUMBNAIL
			, TR_PHOTO
			, CLASS_PHOTO
			, CL_WRITER
			, CL_CATEGORY
   	 )
    VALUES
    (
    		  'CL' || CL.NEXTVAL
    		, #{cafeNo}
    		, #{className}
    		, #{classDes}
    		, #{trName}
    		, #{trIntro}
    		, #{classCurri}
    		, #{studentMax}
    		, #{classDate}
    		, #{classLoca}
    		, #{clPrice}
    		, DEFAULT
    		, DEFAULT
    		, #{busNo}
    		, #{clShortDes}
    		, #{classTime}
    		, #{classTime2}
    		, #{classTime3}
    		, #{classLvl}
    		, #{clRuntime} 
    		, #{clThumbnail}
    		, #{trPhoto}
    		, #{clPhoto}
    		, #{clWriter}
    		, #{category}
    )

mapper.update:
    UPDATE  
   			COFFEECLASS
       SET	
			  CL_NAME = #{className}
			, CL_DES  = #{classDes}
			, TR_NAME = #{trName}
			, TR_INTRO= #{trIntro}
			, CL_CURI = #{classCurri}
			, CL_MAX  = #{studentMax}
			, CL_DATE = #{classDate}
			, CL_LOCA = #{classLoca}
			, CL_PRICE= #{clPrice}
			, CL_SHORTDES = #{clShortDes}
			, CL_TIME = #{classTime}
			, CL_LEVEL= #{classLvl}
			, CL_RUNTIME = #{clRuntime}
			, CL_THUMBNAIL = #{clThumbnail}
			, TR_PHOTO = #{trPhoto}
			, CLASS_PHOTO = #{clPhoto}
	 WHERE
	 		CLASS_NO = #{classNo}

mapper.insert:
	INSERT
      INTO    REPORT
     (
			    REPORT_NO
			  , REPORT_TYPE
			  , REPORT_REASON
			  , ISWARNED
			  , ISCHECKED
			  , REPORTER_ID
			  , ITEM_CODE
			  , REPORTEE_ID 
   	 )
    VALUES
    (
    		    'USRP' || CL_REPORT_SEQ.NEXTVAL
    		  , #{rpReason}
    		  , #{rpDeets}
    		  , DEFAULT
    		  , DEFAULT 	
    		  , #{reporter}
    		  , #{classNo}
    		  , #{reportee}
    )

mapper.select:

     SELECT
			*
	  FROM  COFFEECLASS

			   CL_NAME LIKE #{sv}
			OR CL_DES LIKE #{sv}
			OR TR_NAME LIKE #{sv}
			OR TR_INTRO LIKE #{sv}
			OR CL_CURI LIKE #{sv}
			OR CL_LOCA LIKE #{sv}
			OR CL_SHORTDES LIKE #{sv}					

  ORDER BY CLASS_NO DESC

