	  WHERE   CL_QNA_NO = #{qnaNo}

mapper.select:

 	SELECT 
 			COUNT(*)
 	  FROM 
 	  		COFFEECLASS

			   CL_NAME LIKE #{sv}
			OR CL_DES LIKE #{sv}
			OR TR_NAME LIKE #{sv}
			OR TR_INTRO LIKE #{sv}
			OR CL_CURI LIKE #{sv}
			OR CL_LOCA LIKE #{sv}
			OR CL_SHORTDES LIKE #{sv}					

  ORDER BY CLASS_NO
