mapper.insert:
INSERT
  INTO
       MEMBER
  (
       ID
     , PWD
     , EMAIL
     , NAME
     , PHONE
     , ADDR
     , ENDATE
     , MODATE
     , ISACTIVE
     , GRADE
  )
VALUES
(
       #{id}
     , #{pwd}
     , #{email}
     , #{name}
     , #{phone}
     , #{addr}
     , SYSDATE
     , SYSDATE
     , DEFAULT
     , #{grade}
)

INSERT
  INTO
       CAFE
  (
       C_CODE
     , C_NAME
     , C_OWNER
     , C_NUMBER
     , C_ADDRESS
     , C_STATUS
     , C_LA
     , C_LO
     , ID
  )
VALUES
(
       'CA' || CA.NEXTVAL
     , #{c_name}
     , #{c_owner}
     , #{c_number}
     , #{c_address}
     , DEFAULT
     , #{c_la}
     , #{c_lo}
     , #{id}
)
