mapper.update:
UPDATE
       MEMBER
   SET
       PWD = #{pwd}
     , EMAIL = #{email}
     , NAME = #{name}
     , PHONE = #{phone}
     , ADDR = #{addr}
     , MODATE = SYSDATE
 WHERE
       ID = #{id}

mapper.insert:
INSERT
  INTO
       AD
  (
       AD_CODE
     , APPLYDATE
     , POSTDATE
     , DURATION
     , ORIGIN_IMAGE
     , RENAME_IMAGE
     , IMAGE
     , URL
     , AD_STATUS
     , ID
     , BN_CODE
  )
VALUES
(
       SEQ_AD.NEXTVAL
     , SYSDATE
     , TO_DATE(#{ad_reject}, 'yy/mm/dd')
     , #{duration}
     , #{origin_image}
     , #{rename_image}
     , #{image}
     , #{url}
     , DEFAULT
     , #{id}
     , #{bn_code}
)

mapper.update:
UPDATE
       AD
   SET
       APPLYDATE = SYSDATE
     , POSTDATE = TO_DATE(#{ad_reject}, 'yy/mm/dd')
     , ORIGIN_IMAGE = #{origin_image}
     , RENAME_IMAGE = #{rename_image}
     , IMAGE = #{image}
     , URL = #{url}
     , AD_STATUS = DEFAULT
     , AD_REJECT = NULL
 WHERE
       AD_CODE = #{ad_code}
