mapper.update:
UPDATE
       MEMBER
   SET
       PWD = #{pwd}
     , EMAIL = #{email}
     , NAME = #{name}
     , PHONE = #{phone}
     , ADDR = #{addr}
     , MODATE = SYSDATE
 WHERE
       ID = #{id}

mapper.insert:
INSERT
  INTO
       AD
  (
       AD_CODE
     , APPLYDATE
     , POSTDATE
     , DURATION
     , ORIGIN_IMAGE
     , RENAME_IMAGE
     , IMAGE
     , URL
     , AD_STATUS
     , ID
     , BN_CODE
  )
VALUES
(
       SEQ_AD.NEXTVAL
     , SYSDATE
     , TO_DATE(#{ad_reject}, 'yy/mm/dd')
     , #{duration}
     , #{origin_image}
     , #{rename_image}
     , #{image}
     , #{url}
     , DEFAULT
     , #{id}
     , #{bn_code}
)

mapper.update:
UPDATE
       AD
   SET
       APPLYDATE = SYSDATE
     , POSTDATE = TO_DATE(#{ad_reject}, 'yy/mm/dd')
     , ORIGIN_IMAGE = #{origin_image}
     , RENAME_IMAGE = #{rename_image}
     , IMAGE = #{image}
     , URL = #{url}
     , AD_STATUS = DEFAULT
     , AD_REJECT = NULL
 WHERE
       AD_CODE = #{ad_code}

mapper.insert:
INSERT
  INTO PR_REVIEW
  (
       REVIEW_NO
     , REVIEW_DATE
     , REVIEW_GRADE
     , REVIEW_CONTENT
     , REVIEW_PHOTO
     , REVIEW_CHPH
     , REVIEW_PATH
     , REVIEW_STATUS
     , PURCHASE_NUMBER
     , SUBSCRIBE_CODE
     , ID
  )
VALUES
(
       'STRV' || RV.NEXTVAL
     , SYSDATE
     , #{review_grade}
     , #{review_content}
     , #{review_photo}
     , #{review_chph}
     , #{review_path}
     , DEFAULT
     , #{purchase_number}
     , #{subscribe_code}
     , #{id}
)

INSERT
  INTO CL_REVIEW
  (
       CL_REVIEW_NO
     , CL_PAY_NO
     , CLASS_NO
     , WRITER_ID
     , CL_GRADE
     , CL_REV_IMG
     , CL_REVIEW
     , BLIND
     , CL_REV_DATE
     , CL_REV_RENAME
     , CL_REV_PATH
  )
VALUES
(
       'CLRV' || RV.NEXTVAL
     , #{cl_pay_no}
     , #{class_no}
     , #{writer_id}
     , #{cl_grade}
     , #{cl_rev_img}
     , #{cl_review}
     , DEFAULT
     , SYSDATE
     , #{cl_rev_rename}
     , #{cl_rev_path}
)

INSERT
  INTO CAREVIEW
  (
       C_REV_NO
     , C_REV_DATE
     , C_REV_GRA
     , C_REV_CON
     , C_REV_INAME
     , C_REV_ICHNAME
     , C_REV_IPATH
     , C_REV_STA
     , C_CODE
     , ID
     , C_RES
  )
VALUES
(
       'CARV' || RV.NEXTVAL
     , SYSDATE
     , #{c_rev_gra}
     , #{c_rev_con}
     , #{c_rev_iname}
     , #{c_rev_ichname}
     , #{c_rev_ipath}
     , DEFAULT
     , #{c_code}
     , #{id}
     , #{c_res}
)

mapper.select:
SELECT
       COUNT(*)
  FROM
       PR_REVIEW
 WHERE
       ID = #{id}

   AND PURCHASE_NUMBER = #{purchase_number}


   AND SUBSCRIBE_CODE = #{subscribe_code}

SELECT
       COUNT(*)
  FROM
       CL_REVIEW
 WHERE
       WRITER_ID = #{writer_id}
   AND CLASS_NO = #{class_no}
   AND CL_PAY_NO = #{cl_pay_no}

SELECT
       COUNT(*)
  FROM
       CAREVIEW
 WHERE
       ID = #{id}
   AND C_CODE = #{c_code}
   AND C_RES = #{c_res}
