mapper.insert:
		INSERT
		INTO PRODUCT
		(
		       PR_CODE
		     , ST_CATAGORY
		     , PR_NAME
		     , PR_IF
		     , PR_EVENT
		     , ORIGIN
		     , PR_PRICE
		     , SUBSCRIPTIONS
		     , STORE_PHOTO1
		     , STORE_PHOTO2
		     , STORE_PHOTO3
		     , DY_COMPANY
		     , PR_INF
		     , SERVICE_CENTER
		     , PR_DATE
	         , PR_NOTICE 
      		 , PR_STATUS
      		 , C_CODE
      		 , SCOUNT

		 	)
		VALUES
		(
			STA.NEXTVAL
			,#{ST_CATAGORY}
			,#{PR_NAME}
			,#{PR_IF}
			,#{PR_EVENT}
			,#{ORIGIN}
			,#{PR_PRICE}
			,#{SUBSCRIPTIONS}
			,#{STORE_PHOTO1}
			,#{STORE_PHOTO2}
			,#{STORE_PHOTO3}
			,#{DY_COMPANY}
			,#{PR_INF}
			,#{SERVICE_CENTER}
			, SYSDATE
			,#{PR_NOTICE }
			, 'Y'
			, (SELECT C_CODE
					FROM
   				 CAFE
 				WHERE
   				 ID= #{C_CODE})
			, 0 
		)

mapper.update:
	UPDATE 
	       PRODUCT
	   SET    
	       ST_CATAGORY = #{ ST_CATAGORY }
	     , PR_NAME = #{ PR_NAME }
	     , PR_IF = #{ PR_IF  }
	     , PR_EVENT = #{ PR_EVENT }
	     , ORIGIN = #{ ORIGIN }
	     , PR_PRICE = #{ PR_PRICE } 
	     , STORE_PHOTO1 = #{ STORE_PHOTO1 }
	     , STORE_PHOTO2 = #{ STORE_PHOTO2 }
	     , STORE_PHOTO3 = #{ STORE_PHOTO3 }
	     , DY_COMPANY = #{ DY_COMPANY }
	     , PR_INF = #{ PR_INF }
	     , SERVICE_CENTER = #{ SERVICE_CENTER } 
         , PR_NOTICE = #{ PR_NOTICE }
	 WHERE 
	       PR_CODE = #{ PR_CODE }

mapper.insert:
INSERT
INTO PRODUCT_Q
(
     QNA_NO
    ,QNA_TYPE
    ,QNA_CONTENT
    ,QNA_DATE
    ,PR_CODE 
    ,ID

 	)
VALUES
(

	  STQ.NEXTVAL	
   	, #{qnaTitle}
	, #{qnaContent}
	, SYSDATE
	, #{PR_CODE}
	, #{id} 
)

mapper.select:
SELECT * FROM PRODUCT_Q
AND PR_CODE LIKE #{sv}
AND PR_CODE LIKE #{sv}

SELECT * FROM PRODUCT_A JOIN PRODUCT_Q USING(QNA_NO)
AND PR_CODE LIKE #{sv}
AND PR_CODE LIKE #{sv}

